My solutions:<p><pre><code>  function getUrlContents($url, $showError = true)
  {
	for($x = 0; $x < 5 && !$content; $x++)
		$content = @file_get_contents($url);
	if(!$content && $showError)
		br("Unable to retrieve content for '<B>$url</B>'.");
	return $content;
  }

  function getUrlHandle($url, $context = NULL, $showError = true, $retries = 5)
  {
	$url = str_replace(' ', '%20', $url);
	for($x = 0; $x < $retries && !$handle; $x++)
		if($context)
			$handle = @fopen($url, 'r', false, $context);
		else
			$handle = @fopen($url, 'r');
	if(!$handle && $showError)
		br("\t - Unable to access '<B>$url</B>'.");
	return $handle;
  }

  function br($text = '')
  {
	echo $text;
	if($_SERVER['REQUEST_URI'])
		echo '<BR>';
	echo "\n";
  }</code></pre>

