  function(){
    var%20s=document.createElement(&#x27;script&#x27;);
    s.setAttribute(&#x27;src&#x27;,&#x27;<a href="https://togetherjs.com/togetherjs-min.js&#x27;);" rel="nofollow">https://togetherjs.com/togetherjs-min.js&#x27;);</a>
    document.getElementsByTagName(&#x27;body&#x27;)[0].appendChild(s);
    setTimeout(function(){
    TogetherJS(this);
