public function getYourPitchforks($year, $money, $housing, $safety, $politics, $scapegoat) {
if($politics[$year][&#x27;money&#x27;] > $politics[$year-1][&#x27;money&#x27;]){
unset($scapegoat[$politics[&#x27;id&#x27;]]);
ksort($scapegoat[&#x27;money&#x27;]);
$scapegoat[0][&#x27;money&#x27;]--;
$scapegoat[0][&#x27;housing&#x27;]--;
$scapegoat[0][&#x27;safety&#x27;]--;
}
$year++;
$money--;
$housing--;
$safety--;
if(count($scapegoat)){
$this->getYourPitchforks($year, $money, $housing, $safety, $politics, $scapegoat);
} else {
return array(&#x27;money&#x27;=>$money,&#x27;housing&#x27;=>$housing,&#x27;safety&#x27;=>$safety);
}
}

