                  $_ ~~ /i/ and $¢.worryobs(&#x27;/i&#x27;,&#x27;:i&#x27;);
                  $_ ~~ /g/ and $¢.worryobs(&#x27;/g&#x27;,&#x27;:g&#x27;);
                  $_ ~~ /m/ and $¢.worryobs(&#x27;/m&#x27;,&#x27;^^ and $$ anchors&#x27;);
