    func handler(w http.ResponseWriter, r *http.Request) {
        fmt.Fprintf(w, &quot;Hi there, I love %s!&quot;, r.URL.Path[1:])
    }

    func main() {
        http.HandleFunc(&quot;/&quot;, handler)
        http.ListenAndServe(&quot;:8080&quot;, nil)
    }
