That ggi file is a self-extracting bash script.<p><pre><code>    #!/bin/bash
    echo &quot;&quot;
    echo &quot; FInstaller 1.0&quot;
    echo &quot;-----------------------&quot;

    export TMPDIR=`mktemp -d /tmp/selfextract.XXXXXX`

    ARCHIVE=`awk &#x27;/^__ARCHIVE_BELOW__/ {print NR + 1; exit 0; }&#x27; $0`

    echo &quot;&quot;
    echo &quot;Extracting Installation Files...&quot;
    echo &quot;&quot;

    tail -n+$ARCHIVE $0 | tar xzv -C $TMPDIR

    echo &quot;&quot;
    echo &quot;Launching Installer...&quot;
    echo &quot;&quot;

    CDIR=`pwd`
    echo CDIR
    echo $CDIR
    echo TMPDIR
    echo $TMPDIR
    cd $TMPDIR
    ./installer

    cd $CDIR
    rm -rf $TMPDIR

    exit 0

    __ARCHIVE_BELOW
</code></pre>
