if {[llength $argv] < 1} {
    puts &quot;Usage: ./joinmods3 <\[file\].joinmods2> 

&quot;
    exit
}<p>set HOME        [pwd]
set DATABASE    [lindex $argv 0]
set DIM         10000<p>proc CalcLine {line} {<p><pre><code>    set termData [lindex $line 1]
    set termDataLength [llength $termData]

    foreach {x y} $termData {
        set score1 0
        set score2 0
        set score3 0
        foreach sData [split $y {:}] {
            set sd [split $sData {,}]
            set score1 [expr {$score1+[lindex $sd 0]}]
            set score2 [expr {$score2+[lindex $sd 1]}]
            set score3 [expr {$score3+[lindex $sd 2]}]
        }
        lappend newLine &quot;$x [format %.4f [expr {($score1+$score2)/$score3}]]&quot;
    }

    set returnVal &quot;[lindex $line 0] [list [string map {\{ &quot;&quot; \} &quot;&quot;} [lsort -real -index 1 -decreasing $newLine]]]&quot;</code></pre>
}<p>proc Invoke {} {
    global HOME
    global DATABASE<p><pre><code>    set lCount 10
    set wCount 0
    set wData &quot;&quot;

    set f1 [open $DATABASE &quot;r&quot;]
    set f2 [open ${DATABASE}.j3 &quot;TRUNC CREAT RDWR&quot;]

    while {[gets $f1 wordByte] >= 0} {
        incr wCount

        append wData [CalcLine $wordByte]


        if {$wCount >= $lCount} {
            puts -nonewline $f2 $wData
            set lCount [expr {$lCount + 10}]
            set wData &quot;&quot;
        }
    }

    puts -nonewline $f2 $wData

    close $f1
    close $f2</code></pre>
