Double-bass metal<p>var patch = [
	{steps:11,pulses:13,offset:2},
	{steps:8,pulses:2,offset:6},
	{steps:5,pulses:7,offset:4},
	{steps:1,pulses:3,offset:1},
	{steps:15,pulses:8,offset:8},
];
patch.forEach(function(hit,index){  
	index = index + 1;
	$(&#x27;.space-&#x27;+index).val(hit.steps).trigger(&#x27;change&#x27;);
	$(&#x27;.pulses-&#x27;+index).val(hit.pulses).trigger(&#x27;change&#x27;);
	$(&#x27;.offset-&#x27;+index).val(hit.offset).trigger(&#x27;change&#x27;);
});
