  #include <stdio.h>

  int compare( int x, int y )
  {
      return x - y;
  }

  void testCompare( int x, int y )
  {
      if ( compare( x, y ) < 0 )
      {
          printf( &quot;%d is less than %d
&quot;, x, y );
      }
      else
      {
          printf( &quot;%d is greater than or equal to %d
&quot;, x, y );
      }
  }

  int main()
  {
      testCompare( 5, 10 );
      testCompare( 10, 5 );
      testCompare( 1987654321, -1987654321 );
      return 0;
  }
