Pretty much the same minimal http server in C:<p><pre><code>    #include <WinSock.h>
    #include <stdio.h>
    #pragma comment(lib, &quot;wsock32.lib&quot;)
    int main(int argc, const char *argv[]) {
        WSADATA wsadata;
        WSAStartup(2, &wsadata);
        sockaddr_in address;
        memset(&address, 0, sizeof(address));
        address.sin_family      = AF_INET;
        address.sin_addr.s_addr = inet_addr(&quot;0.0.0.0&quot;);
        address.sin_port        = htons(80);
        int sock = socket(PF_INET, SOCK_STREAM, IPPROTO_TCP);
        bind(sock, (struct sockaddr *)&address, sizeof(address));
        for(;;) {
            listen(sock, 0);
            int connection = accept(sock, NULL, NULL);
            char recvBuffer[1024];
            int recvSize = recv(connection, recvBuffer, sizeof(recvBuffer)-1, 0);
            recvBuffer[recvSize]=0;
            printf(recvBuffer);
            char response[] = &quot;HTTP/1.1 200 OK\nContent-Type: text/html\n\nlol&quot;;
            send(connection, response, sizeof(response), 0);
            closesocket(connection);
        }
        return 0;
    }</code></pre>

